            </tr>

            <tr>
              <th scope="row">4</th>
              {this.props.randomHero.talents[4].map(talent => (
                <Talent
                  key={uuidv4()}
                  talent={talent.icon}
                  randomHero={this.props.randomHero}
                  selected={talent.sort === randomNumber ? true : false}
                />
              ))}
            </tr>

