import React, { Component } from "react";

export default class talentTable extends Component {
  state = {
    level: 1,
    talent: 0,
    selected: false
  };

  // Function for getting random talents
  getRandomTalents = e => {
    e.preventDefault();
    const { talent } = this.state;
    const min = 0;
    const max = 3;
    this.setState({
      talent: Math.floor(Math.random() * (+max - +min)) + +min
    });
  };

  render() {
    return (
      <button
        type="button"
        className="btn btn-primary"
        onClick={this.getRandomTalents}
      >
        Start
      </button>
    );
  }
}
