import React, { Component } from "react";
import HeroList from "./HeroList";
import TalentTable from "./TalentTable";
import HeroHeader from "./HeroHeader";

export default class HeroApp extends Component {
  state = {
    availableHeroes: [
      "abathur",
      "alarak",
      "alexstrasza",
      "anubarak",
      "artanis",
      "arthas",
      "auriel",
      "azmodan",
      "blaze",
      "brightwing",
      "thebutcher",
      "cassia",
      "chen",
      "chogall",
      "chromie",
      "deckard",
      "dehaka",
      "diablo",
      "dva",
      "falstad",
      "fenix",
      "gall",
      "garrosh",
      "gazlowe",
      "genji",
      "greymane",
      "guldan",
      "hanzo",
      "illidan",
      "imperius",
      "jaina",
      "johanna",
      "junkrat",
      "kaelthas",
      "kelthuzad",
      "kerrigan",
      "kharazim",
      "leoric",
      "lili",
      "liming",
      "thelostvikings",
      "ltmorales",
      "lucio"
    ],
    heroes: [],
    randomHero: "",
    visibility: false,
    talentTiers: [1, 4, 7, 10, 13, 16, 20]
  };

  getTalentTableStyle = () => {
    return {
      display: this.state.visibility ? "none" : "table"
    };
  };

  componentDidMount() {
    const url = "https://heroespatchnotes.github.io/heroes-talents/hero/";
    this.state.availableHeroes.map(hero =>
      fetch(url + `${hero}.json`)
        .then(res => res.json())
        .then(result => {
          this.setState({
            heroes: this.state.heroes.concat(result)
          });
        })
    );
    this.setState({
      people: this.state.heroes.filter(hero => {
        return hero !== null;
      })
    });
  }

  // this.setState({
