import React, { Component } from "react";
import HeroList from "./HeroList";
import TalentTable from "./TalentTable";

export default class HeroApp extends Component {
  state = {
    heroes: [],
    randomTalents: [],
    tiers: [1, 4, 7, 10, 13, 16, 20]
  };

  componentDidMount() {
    fetch("https://hotsapi.net/api/v1/heroes")
      .then(res => res.json())
      .then(result => {
        this.setState({ heroes: result });
      });
  }

  // Function for getting random talents
  handleRandomButton = e => {
    e.preventDefault();
    const min = 0;
    const maxHero = this.state.heroes.length;

    for (let i = 0; i < this.state.tiers.length; i++) {
      let array = this.state.heroes.talents.filter(
        talent => talent.level === this.state.tiers[i]
      );
      const maxTalent = array.length;
      const randomTalent =
        array[Math.floor(Math.random() * (+maxTalent - +min)) + +min].name;
      this.setState({ randomTalents: { randomTalent } });
    }
  };

  render() {
    const { level, talent, selected } = this.state;
    const values = { level, talent, selected };
    return (
      <React.Fragment>
        <div className="wrapper">
          <div className="header p-3 mb-2 bg-dark">
            <h1 className="text-center text-light">HOTS Bravery</h1>
          </div>

          <div className="row justify-content-center">
            <TalentTable
              heroes={this.state.heroes}
              onRandomButton={this.handleRandomButton}
            />
          </div>

          <HeroList heroes={this.state.heroes} />
        </div>
      </React.Fragment>
    );
  }
}
