            type: "Trait",
            sort: 4,
            abilityId: "Abathur|D1",
            abilityLinks: ["Abathur|D1"]
          }
        ]
      }
    }
  };

  componentDidMount() {
    const url = "https://heroespatchnotes.github.io/heroes-talents/hero/";
    this.state.availableHeroes.map(hero =>
      fetch(url + `${hero}.json`)
        .then(res => res.json())
        .then(result => {
          this.setState({ heroes: this.state.heroes.concat(result) });
        })
    );

