import React, { Component } from "react";
import HeroList from "./HeroList";
import TalentTable from "./TalentTable";
import uuid from "uuid";
import HeroHeader from "./HeroHeader";
import countFiles from "count-files";

export default class HeroApp extends Component {
  state = {
    availableHeroes: ["abathur", "alarak", "alexstrasza"],
    heroes: [],
    heroesNameID: [],
    numOfHeroes: 87,
    randomHero: "",
    randomHeroTalents: [{ talent: "test", selected: false }],
    tiers: [1, 4, 7, 10, 13, 16, 20],
    selected: ""
  };

  componentDidMount() {
    let promiseArray = [];
    for (let i = 0; i < this.state.availableHeroes.length; i++) {
      let dataUrlLoop = "https://heroespatchnotes.github.io/heroes-talents/hero/"`${
        this.state.availableHeroes[i]
      }.json`;
      promiseArray.push(fetch(dataUrlLoop).then(response => response.json()));
    }
    return Promise.all(promiseArray);

    // fetch(
    //   "https://raw.githubusercontent.com/heroespatchnotes/heroes-talents/master/raw/json/herodata_75589_enus.json"
    // )
    //   .then(res => res.json())
    //   .then(result => {
    //     this.setState({
    //       heroes: result,
    //       heroesNameID: this.convertToArray(result)
    //     });
    //   });

    // this.state.heroes.map(hero =>
    //   fetch(
    //     "https://heroespatchnotes.github.io/heroes-talents/hero/"`${
    //       hero.short_name
    //     }.json`
    //   )
    //     .then(res => res.json())
    //     .then(result => {
    //       console.log(hero);
    //       this.setState({
    //         heroesTest: this.state.heroesTest.concat(result)
    //       });
    //     })
    // );
  }

  convertToArray = result => {
    let array = [];
    for (var key in result) {
      array.push(key);
    }
    return array;
  };

  // Function for getting random talents
  handleRandomButton = e => {
    e.preventDefault();
    const randomNumber = Math.floor(
      Math.random() * Object.keys(this.state.heroesNameID).length + 1
    );
    const randomHero = this.state.heroesNameID[randomNumber];
    this.setState({
      randomHero: randomHero
    });
  };

  // Function for looping through the heroes available
  loopThroughHeroes = (heroes, value1) => {
    let array = [];
    for (var key in heroes) {
      // skip loop if the property is from prototype
      if (!heroes.hasOwnProperty(key)) continue;

      var obj = heroes[key];
      for (var prop in obj) {
        // skip loop if the property is from prototype
        if (!obj.hasOwnProperty(prop)) continue;
        // your code
        if (prop === value1) {
          array.push(obj[prop].heroSelect);
        }
      }
    }
    return array;
  };

  render() {
    const { level, talent, selected } = this.state;
    const values = { level, talent, selected };
    return (
      <React.Fragment>
        <div className="wrapper">
          <div className="header p-3 mb-2 bg-dark">
            <h1 className="text-center text-light">HOTS Bravery</h1>
          </div>

          <div className="header">
            {/* <HeroHeader randomHero={this.state.randomHero} /> */}
          </div>

          <div className="row justify-content-center">
            <TalentTable
              heroes={this.state.heroes}
              onRandomButton={this.handleRandomButton}
              randomHero={this.state.randomHero}
              selected={this.state.selected}
              isSelected={this.isSelected}
              randomHeroTalents={this.randomHeroTalents}
              loopThroughHeroes={this.loopThroughHeroes}
            />
          </div>

          <div className="container">
            <HeroList
              heroes={this.state.heroes}
              loopThroughHeroes={this.loopThroughHeroes}
            />
          </div>
        </div>
      </React.Fragment>
    );
  }
}
