import React, { Component } from "react";

export default class Hero extends Component {
  state = {
    opacity: false
  };

  formatText = hero => {
    // const formattedName = hero
    //   .replace(/[^a-zA-Z ]/g, "")
    //   .replace(/\s+/g, "-")
    //   .toLowerCase();
    const url =
      "https://heroespatchnotes.github.io/heroes-talents/images/heroes/";
    return `${url}${hero.icon}`;
  };

  changeOpacity() {
    this.setState({ opacity: !this.state.opacity });
  }

  render() {
    let img_class = this.state.opacity ? "0.5" : "1";
    return (
      <img
        className={img_class}
        src={this.formatText(this.props.hero)}
        alt=""
        height="60px"
        style={{ margin: "0.1em", cursor: "pointer" }}
        // onClick={() => this.props.onHeroSelect(this.props.hero)}
        onClick={this.changeOpacity.bind(this)}
      />
    );
  }
}
