import React, { Component } from "react";
import HeroList from "./HeroList";
import TalentTable from "./TalentTable";

export default class HeroApp extends Component {
  state = {
    heroes: [],
    randomHero: "",
    randomTalent: ""
  };

  componentDidMount() {
    fetch("https://hotsapi.net/api/v1/heroes")
      .then(res => res.json())
      .then(result => {
        this.setState({ heroes: result });
      });
  }

  // Function for getting random talents
  handleRandomButton = e => {
    e.preventDefault();
    const min = 0;
    const maxHero = this.state.heroes.length;
    const maxTalent = 4;
    const randomHero = Math.floor(Math.random() * (+maxHero - +min)) + +min;
    const randomTalent = Math.floor(Math.random() * (+maxTalent - +min)) + +min;

    this.setState({ randomHero: this.state.heroes[randomHero].name });
    this.setState({
      randomTalent: this.state.heroes[randomHero].talents[randomTalent].name
    });
    console.log(this.state.heroes.randomHero);
    console.log(this.state.heroes.randomTalent);
  };

  render() {
    const { level, talent, selected } = this.state;
    const values = { level, talent, selected };
    return (
      <React.Fragment>
        <div className="wrapper">
          <div className="header p-3 mb-2 bg-dark">
            <h1 className="text-center text-light">HOTS Bravery</h1>
          </div>

          <div className="row justify-content-center">
            <TalentTable onRandomButton={this.handleRandomButton} />
          </div>

          <div className="container">
            <HeroList heroList={this.state.heroes} />
          </div>
        </div>
      </React.Fragment>
    );
  }
}
