import React, { Component } from "react";
import Hero from "./Hero";

class HeroList extends Component {
  getStyle = () => {
    return {
      flexDirection: "row",
      flexWrap: "wrap"
    };
  };

  // Function for looping through the heroes available
  loopThroughHeroes = (heroes, value) => {
    let array = [];
    for (var key in heroes) {
      // skip loop if the property is from prototype
      if (!heroes.hasOwnProperty(key)) continue;

      var obj = heroes[key];
      for (var prop in obj) {
        // skip loop if the property is from prototype
        if (!obj.hasOwnProperty(prop)) continue;
        // your code
        if (prop === value) {
          array.push(obj[prop].heroSelect);
        }
      }
    }
    return array;
  };

  render() {
    return (
      <div className="container col-12 bg-light">
        <div class="row">
          {this.loopThroughHeroes(this.props.heroes, "portraits").map(hero => (
            <Hero hero={hero} styles={this.getStyle()} />
          ))}
          ;
        </div>
      </div>
    );
  }
}

export default HeroList;
