import React, { Component } from "react";

export default class Hero extends Component {
  formatText = hero => {
    // const formattedName = hero
    //   .replace(/[^a-zA-Z ]/g, "")
    //   .replace(/\s+/g, "-")
    //   .toLowerCase();
    const url =
      "https://raw.githubusercontent.com/heroespatchnotes/heroes-talents/master/raw/images/heroportraits/";
    return `${url}${hero}`;
  };

  render() {
    return <img src={this.formatText(this.props.hero)} height="60x" />;
  }
}
