import React, { Component } from "react";
import Talent from "./Talent";
import uuidv4 from "uuid/v4";
import TalentRow from "./TalentRow";

export default class TalentTable extends Component {
  generateRandomNumber = () => {
    let array = [];
    for (let i = 0; i < 6; i++) {
      this.props.randomHero.talents[this.props.talentTiers[i]].map(talent => {
        array.push(talent);
      });
      console.log(array[i]);
    }
    return Math.floor(Math.random() * array.length + 1);
  };

  render() {
    return (
      <React.Fragment>
        <TalentRow
          heroes={this.props.heroes}
          randomHero={this.props.randomHero}
          talentTier={this.props.talentTiers[0]}
          randomNumber={this.generateRandomNumber()}
        />

        <TalentRow
          heroes={this.props.heroes}
          randomHero={this.props.randomHero}
          talentTier={this.props.talentTiers[1]}
          randomNumber={this.generateRandomNumber()}
