import React, { Component } from "react";

class HeroList extends Component {
  formatText = hero => {
    const formattedName = hero.name
      .replace(/[^a-zA-Z ]/g, "")
      .replace(/\s+/g, "-")
      .toLowerCase();
    const url =
      "https://static.heroesofthestorm.com/gd/3a87a62680f43b26f7dd3d203b77e8cd/heroes/";
    return `${url}${formattedName}/circleIcon.png`;
  };

  render() {
    return (
      <div className="table">
        {this.props.heroList.map(hero => (
          <img src={this.props.formatText(hero)} height="50" />
        ))}
      </div>
    );
  }
}

export default HeroList;
