import React, { Component } from "react";

export default class HeroHeader extends Component {
  render() {
    function randomHeroName() {
      const randomHeroName = this.props.randomHero
        .replace(/[^a-zA-Z ]/g, "")
        .replace(/\s+/g, "-")
        .toLowerCase();
      return randomHeroName;
    }

    return (
      <div>
        <img
          src={
            "https://static.heroesofthestorm.com/gd/3a87a62680f43b26f7dd3d203b77e8cd/heroes/" +
            randomHeroName() +
            "/circleIcon.png"
          }
          alt="https://static.icy-veins.com/forum-files/news/45280-vault-hunter-videos-interactive-skill-trees-and-ways-to-play-280x100.jpg"
        />
        <h2>{this.props.randomHero}</h2>
      </div>
    );
  }
}
