import React, { Component } from "react";
import Talent from "./Talent";
import uuidv4 from "uuid/v4";

export default class TalentTable extends Component {
  getRandomNumber = () => {
    return Math.floor(
      Math.random() * this.props.randomHero.talents[1].length + 1
    );
  };

  render() {
    return (
      <div className="container">
        <table className="table">
          <thead>
            <tr>
              <th scope="col">Level</th>
              <th scope="col">Talent 1</th>
              <th scope="col">Talent 2</th>
              <th scope="col">Talent 3</th>
              <th scope="col">Talent 4</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <th scope="row">1</th>
              {this.props.randomHero.talents[1].map(talent => {
                if (
                  talent.sort ===
                  Math.random() * this.props.randomHero.talents[1].length + 1
                ) {
                  <Talent
                    key={uuidv4()}
                    talent={talent.icon}
                    randomHero={this.props.randomHero}
                    sortNumber={talent.sort}
                    selected={true}
                  />;
                } else {
                  <Talent
                    key={uuidv4()}
                    talent={talent.icon}
                    randomHero={this.props.randomHero}
                    sortNumber={talent.sort}
                    randomNumber={this.getRandomNumber()}
                    selected={false}
                  />;
                }
              })}
            </tr>

            <tr>
              <th scope="row">4</th>
              {this.props.randomHero.talents[4].map(talent => (
                <Talent
                  key={uuidv4()}
                  talent={talent.icon}
                  randomHero={this.props.randomHero}
                  sortNumber={talent.sort}
                  randomNumber={this.getRandomNumber()}
                />
              ))}
            </tr>

            <tr>
              <th scope="row">7</th>
              {this.props.randomHero.talents[7].map(talent => (
                <Talent
                  key={uuidv4()}
                  talent={talent.icon}
                  randomHero={this.props.randomHero}
                  sortNumber={talent.sort}
                  randomNumber={this.getRandomNumber()}
                />
              ))}
            </tr>

            <tr>
              <th scope="row">10</th>
              {this.props.randomHero.talents[10].map(talent => (
                <Talent
                  key={uuidv4()}
                  talent={talent.icon}
                  randomHero={this.props.randomHero}
                  sortNumber={talent.sort}
                  randomNumber={this.getRandomNumber()}
                />
              ))}
            </tr>

            <tr>
              <th scope="row">13</th>
              {this.props.randomHero.talents[13].map(talent => (
                <Talent
                  key={uuidv4()}
                  talent={talent.icon}
                  randomHero={this.props.randomHero}
                  sortNumber={talent.sort}
                  randomNumber={this.getRandomNumber()}
                />
              ))}
            </tr>

            <tr>
              <th scope="row">16</th>
              {this.props.randomHero.talents[16].map(talent => (
                <Talent
                  key={uuidv4()}
                  talent={talent.icon}
                  randomHero={this.props.randomHero}
                  sortNumber={talent.sort}
                  randomNumber={this.getRandomNumber()}
                />
              ))}
            </tr>

            <tr>
              <th scope="row">20</th>
              {this.props.randomHero.talents[20].map(talent => (
                <Talent
                  key={uuidv4()}
                  talent={talent.icon}
                  randomHero={this.props.randomHero}
                  sortNumber={talent.sort}
                  randomNumber={this.getRandomNumber()}
                />
              ))}
            </tr>
          </tbody>
        </table>
      </div>
    );
  }
}
