import React, { Component } from "react";
import Talent from "./Talent";

export default class TalentTable extends Component {
  // Function for looping through the heroes available
  loopThroughHeroes = (heroes, value1) => {
    let array = [];
    for (var key in heroes) {
      // skip loop if the property is from prototype
      if (!heroes.hasOwnProperty(key)) continue;

      var obj = heroes[key];
      for (var prop in obj) {
        // skip loop if the property is from prototype
        if (!obj.hasOwnProperty(prop)) continue;
        // your code
        if (prop === value1) {
          console.log(obj[prop.level1]);
          array.push(obj[prop].level1);
        }
      }
    }
    return array;
  };

  render() {
    return (
      <div className="container">
        <table className="table">
          <thead>
            <tr>
              <th scope="col">Level</th>
              <th scope="col">Talent 1</th>
              <th scope="col">Talent 2</th>
              <th scope="col">Talent 3</th>
              <th scope="col">Talent 4</th>
              <th scope="col">Talent 5</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <th scope="row">1</th>
              {this.loopThroughHeroes(this.props.heroes, "talents")}
              {/* {this.props.heroes
                .filter(hero => hero.name === this.props.randomHero)
                .map(hero =>
                  hero.talents
                    .filter(level => level.level === 1)
                    .map(talent => (
                      <Talent
                        talent={talent}
                        onRandomButton={this.props.handleRandomButton}
                        randomHero={this.props.randomHero}
                        selected={this.props.selected}
                        isSelected={this.props.isSelected}
                      />
                    ))
                )} */}
            </tr>

            <tr>
              <th scope="row">4</th>
            </tr>

            <tr>
              <th scope="row">7</th>
            </tr>

            <tr>
              <th scope="row">10</th>
            </tr>

            <tr>
              <th scope="row">13</th>
            </tr>

            <tr>
              <th scope="row">16</th>
            </tr>

            <tr>
              <th scope="row">20</th>
            </tr>
          </tbody>
        </table>
        <button
          type="button"
          className="btn btn-primary"
          onClick={this.props.onRandomButton}
          onChange={this.props.randomHero}
        >
          Start
        </button>
      </div>
    );
  }
}
