import React, { Component } from "react";

export default class Hero extends Component {
  formatText = hero => {
    const formattedName = hero
      .replace(/[^a-zA-Z ]/g, "")
      .replace(/\s+/g, "-")
      .toLowerCase();
    const url =
      "https://raw.githubusercontent.com/heroespatchnotes/heroes-talents/master/raw/images/heroportraits/storm_ui_glues_draft_portrait_";
    return `${url}${hero}.png`;
  };

  render() {
    return <img src={this.formatText(this.props.hero)} height="60x" />;
  }
}
