import React, { Component } from "react";

export default class talentTable extends Component {
  state = {
    level: 1,
    talent: 0,
    selected: false
  };

  // Function for getting random talents
  getRandomTalents = e => {
    e.preventDefault();
    const { talent } = this.state;
    const min = 0;
    const max = 3;
    this.setState({
      talent: Math.floor(Math.random() * (+max - +min)) + +min
    });
  };

  render() {
    switch (this.state.talent) {
      case 0:
        return <h1>This is 0 now</h1>;
      case 1:
        return <h1>This is 1 now</h1>;
      case 2:
        return <h1>This is 2 now</h1>;
      case 3:
        return <h1>This is 3 now</h1>;
    }
    return (
      <button
        type="button"
        className="btn btn-primary"
        onClick={this.getRandomTalents}
      >
        Start
      </button>
    );
  }
}
