import React, { Component } from "react";
import Hero from "./hero";

class HeroList extends Component {
  state = {
    heroes: []
  };

  componentDidMount() {
    fetch("https://api.hotslogs.com/Public/Data/Heroes")
      .then(res => res.json())
      .then(result => {
        this.setState({ heroes: result });
      });
  }

  render() {
    return (
      <div>
        {this.state.heroes.map(counter => (
          <Hero />
        ))}
      </div>
    );
  }
}

export default HeroList;
