import React, { Component } from "react";
import HeroList from "./HeroList";
import TalentTable from "./TalentTable";

export default class HeroApp extends Component {
  state = {
    heroes: []
  };

  componentDidMount() {
    fetch("https://hotsapi.net/api/v1/heroes")
      .then(res => res.json())
      .then(result => {
        this.setState({ heroes: result });
      });
  }

  formatText = hero => {
    const formattedName = hero.name
      .replace(/[^a-zA-Z ]/g, "")
      .replace(/\s+/g, "-")
      .toLowerCase();
    const url =
      "https://static.heroesofthestorm.com/gd/3a87a62680f43b26f7dd3d203b77e8cd/heroes/";
    return `${url}${formattedName}/circleIcon.png`;
  };

  render() {
    const { level, talent, selected } = this.state;
    const values = { level, talent, selected };
    return (
      <React.Fragment>
        <div className="wrapper">
          <div className="header p-3 mb-2 bg-dark">
            <h1 className="text-center text-light">HOTS Bravery</h1>
          </div>

          <div className="row justify-content-center">
            <TalentTable values={values} />
            <h1>{this.state.talent}</h1>
          </div>

          <div className="container">
            <HeroList heroList={this.state.heroes} />
          </div>
        </div>
      </React.Fragment>
    );
  }
}
