import React, { Component } from "react";

class HeroList extends Component {
  componentDidMount() {
    const heroes = this.props;
    fetch("https://hotsapi.net/api/v1/heroes")
      .then(res => res.json())
      .then(result => {
        this.setState({ heroes: result });
      });
  }

  formatText = hero => {
    const formattedName = hero.name
      .replace(/[^a-zA-Z ]/g, "")
      .replace(/\s+/g, "-")
      .toLowerCase();
    const url =
      "https://static.heroesofthestorm.com/gd/3a87a62680f43b26f7dd3d203b77e8cd/heroes/";
    return `${url}${formattedName}/circleIcon.png`;
  };

  render() {
    return (
      <div className="table">
        {this.state.heroes.map(hero => (
          <img src={this.formatText(hero)} height="50" />
        ))}
      </div>
    );
  }
}

export default HeroList;
